import Axios from "axios";

export const getPokeDetailInfo = (pokeName) => {
  /* this is a method to fetch the info from an api */
  /*   fetch("https://pokeapi.co/api/v2/pokemon/")
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
    }); */
  return Axios.get(
    `https://pokeapi.co/api/v2/pokemon-species/${pokeName}`
  ).then((res) => {
    /* console.log(typeof resoult.data); */
    return {
      color: res.data.color.name,
      randomtext: res.data.flavor_text_entries.filter((text) =>
        text.language.name.includes("en")
      ),

      evolChain: Axios.get(res.data.evolution_chain.url)?.then((res) =>
        console.log(
          "evolution to",
          res.data /* .chain.evolves_to[0].evolves_to[0].species.name */
          /*  ", come from ",
          res.data.chain.species.name */
        )
      ),
      other: res.data,
    };
  });
};
