import "./App.css";
/* first we need to install npm "install axios" so we can make easier to call from the api */
import Axios from "axios";
import { useEffect, useState } from "react";

function App() {
  /* this is a method to fetch the info from an api */
  /*   fetch("https://pokeapi.co/api/v2/pokemon/")
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
    }); */
  /* this is another way to fetch info from an api */
  Axios.get("https://pokeapi.co/api/v2/pokemon/")
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
    });
  return (
    <div className="App">
      <h1>Pokedex!</h1>
      <p>data</p>
    </div>
  );
}

export default App;
