import { useState } from "react";
import { getPokeInfo } from "../components/getPokeInfo";

export const Main = () => {
  getPokeInfo(props.pokemon).then((val) => console.log(setPokeInfo(val)));
  return (
    <>
      <p>
        This is the home page where all pokemons should appear, or atleast 20..
        but we are working on it
      </p>
      <div className="displaySection">
        {arrayRange(1, 20, 1).map(function (object, i) {
          return <DisplayPokeInfo pokemon={object} />;
        })}
      </div>
    </>
  );
};

const DisplayPokeInfo = (props) => {
  const [pokeInfo, setPokeInfo] = useState({
    name: "",
    id: "",
    species: "",
    img: "",
    type: "",
  });
  getPokeInfo(props.pokemon).then((val) => setPokeInfo(val));

  return (
    <>
      <div className="displayPokemon">
        <img className="pokemonImg" src={pokeInfo.img} />
        <spam className="pokemonId">#{pokeInfo.id}</spam>
        <h1 className="pokemonName">{pokeInfo.species}</h1>
        <h3 className="pokemonType">{pokeInfo.type}</h3>
      </div>
    </>
  );
};
const arrayRange = (start, stop, step) =>
  Array.from(
    { length: (stop - start) / step + 1 },
    (value, index) => start + index * step
  );
