import Axios from "axios";

export const getPokeDetailInfo = (pokeName) => {
  /* this is a method to fetch the info from an api */
  /*   fetch("https://pokeapi.co/api/v2/pokemon/")
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
    }); */
  const asyncEvolTo = async (link) => {
    const res = await Axios.get(link);
    /*   console.log(
      "result: ",
      res.data.chain.evolves_to.map((evol) => evol.species.name)
    ); */
    return res.data.chain.evolves_to.map((evol) => evol.species.name);
  };

  let ans = Axios.get(
    `https://pokeapi.co/api/v2/pokemon-species/${pokeName}`
  ).then((res) => {
    /* console.log(typeof resoult.data); */
    return {
      color: res.data.color.name,
      randomtext: res.data.flavor_text_entries.filter((text) =>
        text.language.name.includes("en")
      ),

      evolTo: asyncEvolTo(res.data.evolution_chain.url),
      /* Axios.get(res.data.evolution_chain.url)
        .then((res) =>
          res.data.chain.evolves_to.map((evol) => evol.species.name)
        )
        .catch((e) => console.log(e)), */

      other: res.data,
    };
  });
  return ans;
};
