import "./App.css";
/* first we need to install npm "install axios" so we can make easier to call from the api */
import Axios from "axios";
import { useEffect, useState } from "react";

function App() {
  /* this is a method to fetch the info from an api */
  /*   fetch("https://pokeapi.co/api/v2/pokemon/")
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
    }); */

  const [pokeName, setPokeName] = useState("");
  const [pokeInfo, setPokeInfo] = useState({});

  const searchPokemon = () => {
    Axios.get(`https://pokeapi.co/api/v2/pokemon/${pokeName}`).then((res) => {
      console.log(res.data);
      setPokeInfo({
        name: pokeName,
        species: res.data.species.name,
        img: res.data.sprites.front_default,
        hp: res.data.stats[0].base_stat,
      });
    });
  };

  return (
    <div className="App">
      <h1>Pokedex!</h1>
      <input
        type={"text"}
        onChange={(event) => {
          setPokeName(event.target.value);
        }}
        placeholder={"who's that pokemon???"}
      />
      <button onClick={searchPokemon}>&#128269;</button>
      <p>{}</p>
    </div>
  );
}

export default App;
