import Axios from "axios";
import { useState, useEffect } from "react";
import { getPokeDetailInfo } from "../components/getPokeDetailInfo";
import { getPokeInfo } from "../components/getPokeInfo";

export const PokeInfo = () => {
  const [pokeName, setPokeName] = useState("");
  const [pokeChosen, setPokeChosen] = useState(false);
  const [pokeInfo, setPokeInfo] = useState({});
  const [pokeMoreInfo, setPokeMoreInfo] = useState({});

  const searchPokemon = () => {
    getPokeInfo(pokeName).then((val) => setPokeInfo(val));
    getPokeDetailInfo(pokeName).then((val) => setPokeMoreInfo(val));

    setPokeChosen(true);
  };
  let random_pokemon_fact = pokeMoreInfo.flavor_text_entries.filter((fact) =>
    fact.language.name.includes("en")
  );
  return (
    <div className="pageInfo">
      <input
        className="PokemonInput"
        type={"text"}
        onChange={(event) => {
          setPokeName(event.target.value);
        }}
        placeholder={"who's that pokemon???"}
      />
      <button onClick={searchPokemon}>&#128269;</button>
      <div className="DisplaySection">
        {!pokeChosen ? (
          <h1> Please choose a pokemon</h1>
        ) : (
          <>
            <div className="detailCard">
              <div className="detailSectionPokeImg">
                <img src={pokeInfo.img} />

                <h1>{pokeInfo.name}</h1>
                <h1>#{pokeInfo.id}</h1>
              </div>
              <div className="DetailSectionInfo">
                <h3>species: {pokeInfo.species}</h3>
                <img src={pokeInfo.img1} />
                {/* <h3>color: {pokeMoreInfo.color}</h3> */}
                <h3>Type: {pokeInfo.type}</h3>
                <h3>HP: {pokeInfo.hp}</h3>
                <h3>Attack: {pokeInfo.attack}</h3>
                <h3>Defense: {pokeInfo.defense}</h3>

                <h3>
                  fun fact: {random_pokemon_fac.randomtext[0].flavor_text}
                </h3>
                {console.log(pokeMoreInfo)}
                {console.log(pokeMoreInfo.color.name)}
                {console.log()}

                {/* 



                <h3>Height: {pokeMoreInfo.height}</h3> */}
              </div>
            </div>
          </>
        )}{" "}
      </div>
    </div>
  );
};
