import "./App.css";
/* first we need to install npm "install axios" so we can make easier to call from the api */
import Axios from "axios";
import { useEffect, useState } from "react";

function App() {
  /* this is a method to fetch the info from an api */
  /*   fetch("https://pokeapi.co/api/v2/pokemon/")
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
    }); */

  const [pokeName, setPokeName] = useState("");
  const [pokeChosen, setPokeChosen] = useState(false);
  const [pokeInfo, setPokeInfo] = useState({
    name: "",
    id: "",
    species: "",
    img: "",
    hp: "",
    attack: "",
    defense: "",
    type: "",
  });

  const searchPokemon = () => {
    Axios.get(`https://pokeapi.co/api/v2/pokemon/${pokeName}`).then((res) => {
      console.log(res.data);
      setPokeInfo({
        name: pokeName,
        id: res.data.id,
        species: res.data.species.name,
        img: res.data.sprites.front_default,
        hp: res.data.stats[0].base_stat,
        attack: res.data.stats[1].base_stat,
        defense: res.data.stats[2].base_stat,
        type: res.data.types[0].type.name,
      });
      setPokeChosen(true);
    });
  };

  return (
    <div className="App">
      <h1>Pokedex!</h1>

      <input
        type={"text"}
        onChange={(event) => {
          setPokeName(event.target.value);
        }}
        placeholder={"who's that pokemon???"}
      />
      <button onClick={searchPokemon}>&#128269;</button>
      <div className="DisplaySection">
        {!pokeChosen ? (
          <h1> Please choose a pokemon</h1>
        ) : (
          <>
            <h1>{pokeInfo.name}</h1>
            <h1>#{pokeInfo.id}</h1>

            <img src={pokeInfo.img} />
            <h3>species: {pokeInfo.species}</h3>
            <h3>Type: {pokeInfo.type}</h3>
            <h3>species: {pokeInfo.species}</h3>
            <h3>species: {pokeInfo.species}</h3>
            <h3>species: {pokeInfo.species}</h3>
            <h3>species: {pokeInfo.species}</h3>
          </>
        )}{" "}
      </div>
    </div>
  );
}

export default App;
