import Axios from "axios";

export const getPokeDetailInfo = (pokeName) => {
  return Axios.get(
    `https://pokeapi.co/api/v2/pokemon-species/${pokeName}`
  ).then((res) => {
    return {
      color: res.data.color.name,
      randomtext: res.data.flavor_text_entries.filter((text) =>
        text.language.name.includes("en")
      ),

      evolTo: asyncEvolTo(res.data.evolution_chain.url),

      other: res.data,
    };
  });
};

const asyncEvolTo = async (link) => {
  const res = await Axios.get(link);
  return res; /* .data.chain.evolves_to.map((evol) => evol.species.name); */
};
