import "./App.css";
/* first we need to install npm "install axios" so we can make easier to call from the api */
import Axios from "axios";
import { useEffect, useState } from "react";

function App() {
  /* this is a method to fetch the info from an api */
  /*   fetch("https://pokeapi.co/api/v2/pokemon/")
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
    }); */

  const [pokeName, setPokeName] = useState("");
  const [pokeInfo, setPokeInfo] = useState("");

  /*   useEffect(() => { */
  /* this is another way to fetch info from an api */
  /*     Axios.get("https://pokeapi.co/api/v2/pokemon/").then((res) => {
      setPokeInfo(res.data.results[0]);
    });
    Axios.get(pokeInfo.url).then((res) => {
      console.log(res.data.sprites.front_default);
    });
  }, []); */

  const searchPokemon = () => {
    Axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`).then(
      (res) => {
        console.log(res.data);
      }
    );
  };

  return (
    <div className="App">
      <h1>Pokedex!</h1>
      <input
        type={"text"}
        onChange={(event) => {
          setPokeName(event.target.value);
        }}
        placeholder={"who's that pokemon???"}
      />
      <button onClick={searchPokemon}>&#128269;</button>
      <p>{pokeInfo.name}</p>
      <img src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/shiny/1.png" />
    </div>
  );
}

export default App;
